        {/* Status Text */}
        {statusText && (
          <div className="bg-[#00FF00] border-4 border-black p-4 mb-4 transform rotate-[0.5deg] shadow-[6px_6px_0px_0px_rgba(0,0,0,1)] print:hidden">
